                    break;
                case 1:
                    predParserMap0.predState = _SIRIUS_I_PRED_SBX_DP0_BIT;
                    predParserMap1.predState = (_SIRIUS_I_PRED_SBX_DP0_BIT |
                                                _SIRIUS_I_PRED_ALL_IF2_BIT);
