                case 2:
                    predParserMap0.predState = _SIRIUS_I_PRED_SBX_DP1_BIT;
                    predParserMap1.predState = (_SIRIUS_I_PRED_SBX_DP1_BIT |
                                                _SIRIUS_I_PRED_ALL_IF2_BIT);
