    SOC_IF_ERROR_RETURN(WRITE_RB_COS_MAP_TABLE_6r(unit, regval));
    
    soc_reg_field_set(unit, RB_COS_MAP_TABLE_3r, &regval, COS_MAP_31f, 15);
