    SOC_IF_ERROR_RETURN(WRITE_RB_COS_MAP_TABLE_7r(unit, regval));

    SOC_IF_ERROR_RETURN(READ_RB_CI_CONFIGr(unit, &regval));
    soc_reg_field_set(unit, RB_CI_CONFIGr, &regval, MAX_CIf, pInitParams->uDdr3NumMemories - 1);
