    /* reduced memory configuration */
    soc_reg_field_set(unit, RB_CI_CONFIGr, &regval, MAX_CIf, pInitParams->uDdr3NumMemories - 1);
    soc_reg_field_set(unit, RB_CI_CONFIGr, &regval, DRAM_PAGE_SIZEf, 0);
