    soc_reg_field_set(unit, TX_CI_CONFIGr, &regval, NUM_COL_BITSf, pInitParams->uNumColBits);	
    soc_reg_field_set(unit, TX_CI_CONFIGr, &regval, BUFFER_SIZEf, pInitParams->uBufferSize);
